import React from 'react';
import { Box, TextField, Button, Typography, Paper } from '@mui/material';

const Chat = () => {
    return (
        <Box display="flex" flexDirection="column" height="100%">
            <Typography variant="h5" gutterBottom>Chat</Typography>
            <Paper style={{ flex: 1, overflow: 'auto', marginBottom: '16px', padding: '8px' }}>
                {/* Zone d'affichage des messages */}
                {/* Ajoutez ici les composants ou le code pour afficher les messages */}
            </Paper>
            <Box display="flex">
                <TextField label="Message" fullWidth variant="outlined" margin="normal" />
                <Button variant="contained" color="primary" style={{ marginLeft: '8px', marginTop: '16px', height: '56px' }}>Envoyer</Button>
            </Box>
        </Box>
    );
};

export default Chat;
