import axios from 'axios';

// Configurer l'instance d'Axios avec l'URL de base du back-end
const api = axios.create({
    baseURL: 'http://localhost:3333' // Assurez-vous que le serveur AdonisJS fonctionne sur ce port
});

export default api;
