import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, AppBar, Tabs, Tab, Box, Typography } from '@mui/material';
import api from '../services/api';
import Chat from './Chat';
import CharacterSheet from './CharacterSheet';
import Items from './Items';
import Journals from './Journals';
import GameManagement from './GameManagement';

const GameEnvironment = () => {
    const { id } = useParams();
    const [value, setValue] = useState(0);
    const [game, setGame] = useState(null);

    useEffect(() => {
        const fetchGameDetails = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await api.get(`/api/game/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setGame(response.data.data);
            } catch (error) {
                console.error('Error fetching game details:', error);
            }
        };

        fetchGameDetails();
    }, [id]);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    if (!game) {
        return <Typography variant="h5">Chargement des d√©tails de la partie...</Typography>;
    }

    return (
        <Container maxWidth="lg">
            <AppBar position="static">
                <Tabs value={value} onChange={handleChange}>
                    <Tab label="Chat" />
                    <Tab label="Fiches de Personnage" />
                    <Tab label="Objets" />
                    <Tab label="Journaux" />
                    <Tab label="Gestion de Partie" />
                </Tabs>
            </AppBar>
            <Typography variant="h4" component="h1" gutterBottom>
                {game.name}
            </Typography>
            <Typography variant="body1" gutterBottom>
                {game.description}
            </Typography>
            <TabPanel value={value} index={0}>
                <Chat />
            </TabPanel>
            <TabPanel value={value} index={1}>
                <CharacterSheet />
            </TabPanel>
            <TabPanel value={value} index={2}>
                <Items />
            </TabPanel>
            <TabPanel value={value} index={3}>
                <Journals />
            </TabPanel>
            <TabPanel value={value} index={4}>
                <GameManagement />
            </TabPanel>
        </Container>
    );
};

const TabPanel = (props) => {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`tabpanel-${index}`}
            aria-labelledby={`tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box p={3}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    );
};

export default GameEnvironment;
