import React, { useState, useEffect } from 'react';
import { Box, Tabs, Tab, Paper } from '@mui/material';
import Chat from './Chat';
import Characters from './CharacterSheet';
import Items from './Items';
import Notes from './Journal';
import GameManagement from './GameManagement';

const GameEnvironment = () => {
    const [tabIndex, setTabIndex] = useState(0);
    const [fileType, setFileType] = useState('video'); // 'image' or 'video'

    // Lien en dur vers la vidéo sur le back-end
    const filePath = 'http://localhost:3333/videos/video.m4v';

    useEffect(() => {
        const determineFileType = (path) => {
            const extension = path.split('.').pop();
            if (['mp4', 'm4v', 'webm', 'ogg'].includes(extension)) {
                return 'video';
            } else if (['jpg', 'jpeg', 'png', 'gif'].includes(extension)) {
                return 'image';
            } else {
                return 'unknown';
            }
        };

        const type = determineFileType(filePath);
        setFileType(type);
    }, [filePath]);

    const handleTabChange = (event, newIndex) => {
        setTabIndex(newIndex);
    };

    return (
        <Box className="game-environment">
            <Box className="game-map">
                {fileType === 'video' && (
                    <video width="100%" autoPlay loop controls>
                        <source src={filePath} type="video/mp4" />
                        Your browser does not support the video tag.
                    </video>
                )}
                {fileType === 'image' && (
                    <img src={filePath} alt="Map" className="game-map-image" />
                )}
                {fileType === 'unknown' && (
                    <p>Unsupported file type</p>
                )}
            </Box>
            <Box className="game-sidebar">
                <Paper>
                    <Tabs
                        value={tabIndex}
                        onChange={handleTabChange}
                        indicatorColor="primary"
                        textColor="primary"
                        centered
                    >
                        <Tab label="Chat" />
                        <Tab label="Personnages" />
                        <Tab label="Objets" />
                        <Tab label="Notes" />
                        <Tab label="Gestion" />
                    </Tabs>
                </Paper>
                <Box className="tab-content">
                    {tabIndex === 0 && <Chat />}
                    {tabIndex === 1 && <Characters />}
                    {tabIndex === 2 && <Items />}
                    {tabIndex === 3 && <Notes />}
                    {tabIndex === 4 && <GameManagement />}
                </Box>
            </Box>
        </Box>
    );
};

export default GameEnvironment;
