import React from 'react';
import { ListItem, ListItemText, Typography, Box } from '@mui/material';

const Message = ({ user, text, details }) => {
    const isDiceRoll = details !== undefined;

    return (
        <ListItem>
            <ListItemText
                primary={
                    <Typography variant="h6" component="span">
                        {user}
                    </Typography>
                }
                secondary={
                    isDiceRoll ? (
                        <Box>
                            <Typography variant="h4" component="span" style={{ fontWeight: 'bold', display: 'block', textAlign: 'center' }}>
                                {text}
                            </Typography>
                            <Typography variant="body2" component="span" style={{ display: 'block', textAlign: 'center', color: 'gray' }}>
                                {details}
                            </Typography>
                        </Box>
                    ) : (
                        <Typography variant="body1" component="span">
                            {text}
                        </Typography>
                    )
                }
            />
        </ListItem>
    );
};

export default Message;
