import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Typography, Paper, List, ListItem, ListItemText } from '@mui/material';
import { rollDice } from '../utils/diceRoller'; // Assurez-vous que le fichier diceRoller.js est dans le dossier utils

const Chat = ({ userName }) => {
    const [messages, setMessages] = useState([]);
    const [messageInput, setMessageInput] = useState('');

    const handleDiceRoll = (command) => {
        const { result, rolls, total } = rollDice(command);
        return { result, rolls, total };
    };

    const handleSendMessage = () => {
        if (messageInput.trim() !== '') {
            let message = { user: userName, text: messageInput };
            if (messageInput.startsWith('/r ')) {
                const command = messageInput.substring(3);
                const { result, rolls, total } = handleDiceRoll(command);
                message = {
                    user: userName,
                    text: `Résultat: ${total}`,
                    isDiceRoll: true,
                    rolls,
                    total
                };
            }
            setMessages([...messages, message]);
            setMessageInput('');
        }
    };

    const handleInputChange = (event) => {
        setMessageInput(event.target.value);
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
            handleSendMessage();
        }
    };

    const getResultColor = (total, rolls) => {
        if (rolls.includes(1)) {
            return 'red';
        } else if (rolls.includes(20)) {
            return 'green';
        }
        return 'inherit';
    };

    return (
        <Box display="flex" flexDirection="column" height="100%">
            <Typography variant="h5" gutterBottom>Chat</Typography>
            <Paper style={{ flex: 1, overflow: 'auto', marginBottom: '16px', padding: '8px' }}>
                <List>
                    {messages.map((message, index) => (
                        <ListItem key={index}>
                            <ListItemText
                                primary={
                                    <>
                                        {message.user}
                                        {message.isDiceRoll ? (
                                            <Typography variant="h4" component="span" style={{ color: getResultColor(message.total, message.rolls), display: 'block' }}>
                                                {message.text}
                                            </Typography>
                                        ) : (
                                            <Typography component="span" style={{ display: 'block' }}>
                                                {message.text}
                                            </Typography>
                                        )}
                                    </>
                                }
                                secondary={message.isDiceRoll ? `(${message.rolls.join(', ')})` : ''}
                            />
                        </ListItem>
                    ))}
                </List>
            </Paper>
            <Box display="flex" alignItems="center">
                <TextField
                    label="Message"
                    fullWidth
                    variant="outlined"
                    margin="normal"
                    value={messageInput}
                    onChange={handleInputChange}
                    onKeyPress={handleKeyPress}
                />
                <Button
                    variant="contained"
                    color="primary"
                    style={{ marginLeft: '8px', marginTop: '16px', height: '56px' }}
                    onClick={handleSendMessage}
                >
                    Envoyer
                </Button>
            </Box>
        </Box>
    );
};

export default Chat;
