import React, { useState } from 'react';
import { Box, Tabs, Tab, Typography, Paper } from '@mui/material';
import Chat from './Chat';
import Characters from './Characters';
import Items from './Items';
import Notes from './Notes';
import GameManagement from './GameManagement';

const GameEnvironment = () => {
    const [tabIndex, setTabIndex] = useState(0);

    const handleTabChange = (event, newIndex) => {
        setTabIndex(newIndex);
    };

    return (
        <Box className="game-environment">
            <Box className="game-map">
                {/* Ici vous pouvez mettre votre carte de jeu */}
                <img src="/path/to/your/map/image.jpg" alt="Map" className="game-map-image" />
            </Box>
            <Box className="game-sidebar">
                <Paper>
                    <Tabs
                        value={tabIndex}
                        onChange={handleTabChange}
                        indicatorColor="primary"
                        textColor="primary"
                        centered
                    >
                        <Tab label="Chat" />
                        <Tab label="Personnages" />
                        <Tab label="Objets" />
                        <Tab label="Notes" />
                        <Tab label="Gestion" />
                    </Tabs>
                </Paper>
                <Box className="tab-content">
                    {tabIndex === 0 && <Chat />}
                    {tabIndex === 1 && <Characters />}
                    {tabIndex === 2 && <Items />}
                    {tabIndex === 3 && <Notes />}
                    {tabIndex === 4 && <GameManagement />}
                </Box>
            </Box>
        </Box>
    );
};

export default GameEnvironment;
