import React, { useState, useCallback } from 'react';
import { Box, Typography, Dialog, DialogActions, DialogContent, DialogTitle, TextField, Paper, List, ListItem, ListItemText, IconButton, Tooltip, Button } from '@mui/material';
import FolderIcon from '@mui/icons-material/Folder';
import InsertDriveFileIcon from '@mui/icons-material/InsertDriveFile';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import CreateNewFolderIcon from '@mui/icons-material/CreateNewFolder';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const ItemTypes = {
    FOLDER: 'folder',
    FILE: 'file'
};

const DraggableItem = ({ item, index, moveItem, findItem, parentId }) => {
    const originalIndex = findItem(item.id).index;

    const [{ isDragging }, drag] = useDrag({
        type: item.type,
        item: { id: item.id, originalIndex, parentId, type: item.type },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
        end: (dropResult, monitor) => {
            const { id: droppedId, originalIndex } = monitor.getItem();
            const didDrop = monitor.didDrop();
            if (!didDrop) {
                moveItem(droppedId, originalIndex);
            }
        },
    });

    const [, drop] = useDrop({
        accept: [ItemTypes.FOLDER, ItemTypes.FILE],
        canDrop: () => true,
        drop: (draggedItem) => {
            if (draggedItem.type === ItemTypes.FILE && item.type === ItemTypes.FOLDER) {
                moveItem(draggedItem.id, item.id);
            } else if (draggedItem.type === ItemTypes.FOLDER && item.type === ItemTypes.FOLDER) {
                moveItem(draggedItem.id, item.id);
            }
        },
    });

    return (
        <div ref={(node) => drag(drop(node))} style={{ opacity: isDragging ? 0.5 : 1 }}>
            <ListItem style={{ paddingLeft: 20 }}>
                {item.type === 'folder' ? <FolderIcon /> : <InsertDriveFileIcon />}
                <ListItemText primary={item.name} />
                <IconButton edge="end">
                    <DeleteIcon />
                </IconButton>
            </ListItem>
            {item.type === 'folder' && item.children && (
                <List component="div" disablePadding>
                    {item.children.map((child, index) => (
                        <DraggableItem
                            key={child.id}
                            item={child}
                            index={index}
                            moveItem={moveItem}
                            findItem={findItem}
                            parentId={item.id}
                        />
                    ))}
                </List>
            )}
        </div>
    );
};

const CharacterManagement = () => {
    const [structure, setStructure] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);
    const [newItem, setNewItem] = useState({ type: '', name: '', parentId: null });

    const findItem = useCallback((id, items = structure) => {
        let result;
        items.some((item, index) => {
            if (item.id === id) {
                result = { item, index, parent: items };
                return true;
            }
            if (item.children) {
                result = findItem(id, item.children);
                return result !== undefined;
            }
            return false;
        });
        return result;
    }, [structure]);

    const moveItem = useCallback((id, parentId) => {
        const { item, index, parent } = findItem(id);
        const updatedStructure = [...structure];
        parent.splice(index, 1);
        const newParent = parentId === null ? updatedStructure : findItem(parentId).item.children;
        newParent.push(item);
        setStructure(updatedStructure);
    }, [structure, findItem]);

    const handleOpenDialog = (type, parentId = null) => {
        setNewItem({ type, name: '', parentId });
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewItem({ ...newItem, [name]: value });
    };

    const handleCreateItem = () => {
        if (newItem.name.trim() === '') return;

        const updatedStructure = [...structure];
        const newItemObject = {
            id: Date.now(),
            type: newItem.type,
            name: newItem.name,
            children: newItem.type === 'folder' ? [] : undefined,
        };

        if (newItem.parentId === null) {
            updatedStructure.push(newItemObject);
        } else {
            const parent = findItem(newItem.parentId).item;
            parent.children.push(newItemObject);
        }

        setStructure(updatedStructure);
        handleCloseDialog();
    };

    return (
        <DndProvider backend={HTML5Backend}>
            <Box>
                <Typography variant="h5" gutterBottom>Gestion des Personnages</Typography>
                <Paper style={{ padding: 16, marginBottom: 16, display: 'flex', justifyContent: 'flex-end', alignItems: 'center' }}>
                    <Box>
                        <Tooltip title="Ajouter Dossier">
                            <IconButton color="primary" onClick={() => handleOpenDialog('folder')}>
                                <CreateNewFolderIcon />
                            </IconButton>
                        </Tooltip>
                        <Tooltip title="Ajouter Fiche">
                            <IconButton color="secondary" onClick={() => handleOpenDialog('file')}>
                                <AddIcon />
                            </IconButton>
                        </Tooltip>
                    </Box>
                </Paper>
                <List>
                    {structure.map((item, index) => (
                        <DraggableItem
                            key={item.id}
                            item={item}
                            index={index}
                            moveItem={moveItem}
                            findItem={findItem}
                            parentId={null}
                        />
                    ))}
                </List>
                <Dialog open={openDialog} onClose={handleCloseDialog}>
                    <DialogTitle>{`Ajouter un Nouveau ${newItem.type === 'folder' ? 'Dossier' : 'Fiche'}`}</DialogTitle>
                    <DialogContent>
                        <TextField
                            autoFocus
                            margin="dense"
                            label="Nom"
                            name="name"
                            fullWidth
                            value={newItem.name}
                            onChange={handleInputChange}
                        />
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleCloseDialog} color="primary">
                            Annuler
                        </Button>
                        <Button onClick={handleCreateItem} color="primary">
                            Ajouter
                        </Button>
                    </DialogActions>
                </Dialog>
            </Box>
        </DndProvider>
    );
};

export default CharacterManagement;
