import React, { useState } from 'react';
import { Box, TextField, Button, Typography, Paper, List, ListItem, ListItemText } from '@mui/material';

const Chat = () => {
    const [messages, setMessages] = useState([]);
    const [messageInput, setMessageInput] = useState('');
    const userName = "Utilisateur"; // Remplacer par le nom de l'utilisateur réel

    const handleSendMessage = () => {
        if (messageInput.trim() !== '') {
            setMessages([...messages, { user: userName, text: messageInput }]);
            setMessageInput('');
        }
    };

    const handleInputChange = (event) => {
        setMessageInput(event.target.value);
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <Box display="flex" flexDirection="column" height="100%">
            <Typography variant="h5" gutterBottom>Chat</Typography>
            <Paper style={{ flex: 1, overflow: 'auto', marginBottom: '16px', padding: '8px' }}>
                <List>
                    {messages.map((message, index) => (
                        <ListItem key={index}>
                            <ListItemText primary={message.user} secondary={message.text} />
                        </ListItem>
                    ))}
                </List>
            </Paper>
            <Box display="flex" alignItems="center">
                <TextField
                    label="Message"
                    fullWidth
                    variant="outlined"
                    margin="normal"
                    value={messageInput}
                    onChange={handleInputChange}
                    onKeyPress={handleKeyPress}
                />
                <Button
                    variant="contained"
                    color="primary"
                    style={{ marginLeft: '8px', marginTop: '16px', height: '56px' }}
                    onClick={handleSendMessage}
                >
                    Envoyer
                </Button>
            </Box>
        </Box>
    );
};

export default Chat;
