import React, { useEffect, useState } from 'react';
import { Container, Typography, Box, List, ListItem, ListItemText, Button } from '@mui/material';
import NavBar from './NavBar';
import axios from 'axios';

const DeleteGames = () => {
    const [mjGames, setMjGames] = useState([]);

    useEffect(() => {
        const fetchMjGames = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:3333/api/mes-parties-mj', {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setMjGames(response.data.data);
            } catch (error) {
                console.error('Error fetching MJ games:', error);
            }
        };

        fetchMjGames();
    }, []);

    const handleDelete = async (gameId) => {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`http://localhost:3333/api/supprimer-partie/${gameId}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setMjGames(mjGames.filter(game => game.id !== gameId));
            alert('Partie supprimée avec succès');
        } catch (error) {
            console.error('Error deleting game:', error);
            alert('Erreur lors de la suppression de la partie');
        }
    };

    return (
        <>
            <NavBar />
            <Container maxWidth="sm">
                <Box mt={8}>
                    <Typography variant="h4" component="h1" gutterBottom>
                        Supprimer des Parties
                    </Typography>
                    <List>
                        {mjGames.map((game) => (
                            <ListItem key={game.id}>
                                <ListItemText primary={game.name} secondary={game.description} />
                                <Button variant="contained" color="secondary" onClick={() => handleDelete(game.id)}>
                                    Supprimer
                                </Button>
                            </ListItem>
                        ))}
                    </List>
                </Box>
            </Container>
        </>
    );
};

export default DeleteGames;
