// src/components/Chat.js
import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Typography, Paper, List } from '@mui/material';
import Message from './Message';
import api from '../services/api'; // Assurez-vous que l'instance configurée d'Axios est correctement importée

const Chat = () => {
    const [messages, setMessages] = useState([]);
    const [messageInput, setMessageInput] = useState('');
    const [userName, setUserName] = useState('Utilisateur');

    useEffect(() => {
        // Fetch user info
        const fetchUserInfo = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await api.get('/api/user', {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setUserName(response.data.username);
            } catch (error) {
                console.error('Error fetching user info:', error);
            }
        };

        fetchUserInfo();
    }, []);

    const handleSendMessage = () => {
        if (messageInput.trim() !== '') {
            setMessages([...messages, { user: userName, text: messageInput }]);
            setMessageInput('');
        }
    };

    const handleInputChange = (event) => {
        setMessageInput(event.target.value);
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <Box display="flex" flexDirection="column" height="100%">
            <Typography variant="h5" gutterBottom>Chat</Typography>
            <Paper style={{ flex: 1, overflow: 'auto', marginBottom: '16px', padding: '8px' }}>
                <List>
                    {messages.map((message, index) => (
                        <Message key={index} user={message.user} text={message.text} />
                    ))}
                </List>
            </Paper>
            <Box display="flex" alignItems="center">
                <TextField
                    label="Message"
                    fullWidth
                    variant="outlined"
                    margin="normal"
                    value={messageInput}
                    onChange={handleInputChange}
                    onKeyPress={handleKeyPress}
                />
                <Button
                    variant="contained"
                    color="primary"
                    style={{ marginLeft: '8px', marginTop: '16px', height: '56px' }}
                    onClick={handleSendMessage}
                >
                    Envoyer
                </Button>
            </Box>
        </Box>
    );
};

export default Chat;
